import React, { useState, useEffect } from 'react';
import './MovieForm.css';

const MovieForm = ({ movie, onSave, onCancel }) => {
    const [formData, setFormData] = useState({
        title: '',
        director: '',
        casting: '',
        releaseYear: '',
        description: ''
    });

    // Update form when movie prop changes
    useEffect(() => {
        if (movie) {
            setFormData({
                title: movie.title || '',
                director: movie.director || '',
                casting: movie.casting || '',
                releaseYear: movie.releaseYear || '',
                description: movie.description || ''
            });
        } else {
            // Reset form when adding a new movie
            setFormData({
                title: '',
                director: '',
                casting: '',
                releaseYear: '',
                description: ''
            });
        }
    }, [movie]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: name === 'releaseYear' ? parseInt(value, 10) || '' : value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validate form data
        if (!formData.title || !formData.director || !formData.releaseYear) {
            alert('Please fill in all required fields');
            return;
        }

        onSave(formData);

        // Reset form
        if (!movie) {
            setFormData({
                title: '',
                director: '',
                casting: '',
                releaseYear: '',
                description: ''
            });
        }
    };

    return (
        <form className="movie-form" onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="title">Title*</label>
                <input
                    type="text"
                    id="title"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                />
            </div>

            <div className="form-group">
                <label htmlFor="director">Director*</label>
                <input
                    type="text"
                    id="director"
                    name="director"
                    value={formData.director}
                    onChange={handleChange}
                    required
                />
            </div>

            <div className="form-group">
                <label htmlFor="casting">Cast</label>
                <input
                    type="text"
                    id="casting"
                    name="casting"
                    value={formData.casting}
                    onChange={handleChange}
                />
            </div>

            <div className="form-group">
                <label htmlFor="releaseYear">Release Year*</label>
                <input
                    type="number"
                    id="releaseYear"
                    name="releaseYear"
                    value={formData.releaseYear}
                    onChange={handleChange}
                    min="1900"
                    max={new Date().getFullYear()}
                    required
                />
            </div>

            <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                />
            </div>

            <div className="form-actions">
                <button type="submit" className="save-button">
                    {movie ? 'Update Movie' : 'Add Movie'}
                </button>
                <button type="button" className="cancel-button" onClick={onCancel}>
                    Cancel
                </button>
            </div>
        </form>
    );
};

export default MovieForm;
