import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MovieList.css';

const MovieList = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filter states
  const [filters, setFilters] = useState({
    title: '',
    director: '',
    casting: '',
    releaseYear: ''
  });

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prevFilters => ({
      ...prevFilters,
      [name]: value
    }));
  };

  const applyFilters = async () => {
    setLoading(true);
    try {
      // Convert filters to query parameters
      const params = new URLSearchParams();
      if (filters.title) params.append('title', filters.title);
      if (filters.director) params.append('director', filters.director);
      if (filters.casting) params.append('casting', filters.casting);
      if (filters.releaseYear) params.append('releaseYear', filters.releaseYear);

      const response = await axios.get(`/api/movies?${params.toString()}`);

      if (Array.isArray(response.data)) {
        setMovies(response.data);
      } else {
        // Handle non-array response
        console.log("Backend returned non-array data:", response.data);
        setMovies([]);
      }
    } catch (err) {
      setError('Failed to fetch movies');
      console.error('Error fetching movies:', err);
    } finally {
      setLoading(false);
    }
  };

  const resetFilters = () => {
    setFilters({
      title: '',
      director: '',
      casting: '',
      releaseYear: ''
    });
    fetchMovies(); // Reload all movies
  };

  const fetchMovies = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/api/movies');

      if (Array.isArray(response.data)) {
        setMovies(response.data);
      } else {
        // Create sample data for demonstration
        setMovies([
          {
            id: 1,
            title: "The Shawshank Redemption",
            director: "Frank Darabont",
            casting: "Tim Robbins, Morgan Freeman",
            releaseYear: 1994,
            description: "Two imprisoned men bond over a number of years."
          },
          {
            id: 2,
            title: "The Godfather",
            director: "Francis Ford Coppola",
            casting: "Marlon Brando, Al Pacino",
            releaseYear: 1972,
            description: "The aging patriarch of an organized crime dynasty."
          },
          {
            id: 3,
            title: "The Dark Knight",
            director: "Christopher Nolan",
            casting: "Christian Bale, Heath Ledger",
            releaseYear: 2008,
            description: "Batman fights against the Joker."
          }
        ]);
        console.log("Backend returned non-array data, using sample data");
      }
    } catch (err) {
      setError('Failed to fetch movies');
      console.error('Error fetching movies:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, []);

  if (loading) return <div className="loading">Loading movies...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
      <div className="movie-list-container">
        <div className="filter-section">
          <h3>Filter Movies</h3>
          <div className="filter-fields">
            <div className="filter-field">
              <label htmlFor="title">Title:</label>
              <input
                  type="text"
                  id="title"
                  name="title"
                  value={filters.title}
                  onChange={handleFilterChange}
                  placeholder="Search by title"
              />
            </div>

            <div className="filter-field">
              <label htmlFor="director">Director:</label>
              <input
                  type="text"
                  id="director"
                  name="director"
                  value={filters.director}
                  onChange={handleFilterChange}
                  placeholder="Search by director"
              />
            </div>

            <div className="filter-field">
              <label htmlFor="casting">Cast:</label>
              <input
                  type="text"
                  id="casting"
                  name="casting"
                  value={filters.casting}
                  onChange={handleFilterChange}
                  placeholder="Search by cast member"
              />
            </div>

            <div className="filter-field">
              <label htmlFor="releaseYear">Release Year:</label>
              <input
                  type="number"
                  id="releaseYear"
                  name="releaseYear"
                  value={filters.releaseYear}
                  onChange={handleFilterChange}
                  placeholder="e.g. 2008"
              />
            </div>
          </div>

          <div className="filter-buttons">
            <button onClick={applyFilters} className="apply-btn">Apply Filters</button>
            <button onClick={resetFilters} className="reset-btn">Reset</button>
          </div>
        </div>

        <div className="movies-section">
          <h2>Featured Movies</h2>
          {movies.length === 0 ? (
              <p className="no-movies">No movies match your search criteria.</p>
          ) : (
              <div className="movie-grid">
                {movies.map((movie) => (
                    <div key={movie.id} className="movie-card">
                      <h3>{movie.title}</h3>
                      <div className="movie-details">
                        <p><strong>Director:</strong> {movie.director}</p>
                        <p><strong>Cast:</strong> {movie.casting}</p>
                        <p><strong>Year:</strong> {movie.releaseYear}</p>
                      </div>
                      <p className="movie-description">{movie.description}</p>
                    </div>
                ))}
              </div>
          )}
        </div>
      </div>
  );
};

export default MovieList;
